import React from 'react';

const faqData = [
    {

        question: 'How much does it cost to stay at a hostel in Kota?',
        answer: 'The prices for student accommodations vary depending on the facilities. It ranges from Rs.10000/- to Rs.15000/-'
    },
    {
        question: 'How do I install React?',
        answer: 'You can install React using npm or yarn by running `npm install react` or `yarn add react`.'
    },
    // Add more FAQ items as needed
];

export const FAQ = () => {
    return (
        <div id="faq">
            <div className="container">
                <div className='section-title text-center'>
                    <h2>FAQs and General Informations</h2>
                </div>
                <p>Boys and Girls hostels are availabe. </p>
                {faqData.map((faq, index) => (
                    <div key={index}>
                        <h3>{faq.question}</h3>
                        <p>{faq.answer}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};
